using FlyTiger;
namespace ConsoleApp2
{
    [AutoConstructor()]
    partial class Class1
    {
        private int value;

        private string str;

        [AutoConstructorInitialize]
        private void Init()
        {

        }
    }
}

output.code:

namespace ConsoleApp2
{
    partial class Class1
    {
        public Class1(int value, string str)
        {
            this.value = value;
            this.str = str;
            this.Init();
        }
    }
}
